const item = $input.item.json;
const content = item.body?.content?.trim();
if (content) {
  const originalCommand = content.toLowerCase().split(' ').shift();
  item.command = originalCommand?.trim();
  if (item.command)
  {
    if (item.command.startsWith('/')) {
      item.command = item.command.substring(1);
    }

    item.arguments = content.substring(originalCommand.length+1);
    item.arguments = item.arguments.trim();    
  }
}
return $input.item;
